      activated(){  //组件激活时调用。
        console.log('activated');
      },
      deactivated(){  //组件停用时调用。
        console.log('deactivated');
      },
      beforeDestroy(){  //实例销毁之前调用。在这一步，实例仍然完全可用。
        console.log('beforeDestroy');
